select season from athlete_events group by season;
 
 select distinct year from athlete_events;
 
 select count(id) from athlete_events where season='Summer' group by year;
 
 select a.*,b.season_id,c.year_id from athlete_events a
 left join DimSeason b on a.season=b.season
left join DimYear$ c on a.year=c.year;
 
 select a.*,b.season_id,c.year_id from FactPartitipation a 
left join DimSeason b on a.season=b.season
left join DimYear$ c on a.year=c.year;


select * from FactPartitipation$ where NOC is null or Noc not in(select noc from noc_regions);
select * from noc_regions where region is null;

select distinct Medal from FactPartitipation$;
select year,noc,count(noc) from FactPartitipation$ where Medal='Gold' group by noc, year;
